mapper.update:
update board
title=#{title}, subject=#{subject}, category=#{category},
where board_no=#{boardNo}

sql.where:
board_type_no = #{boardTypeNo}

	AND (Title LIKE '%'||#{title}||'%'
	OR subject LIKE '%'||#{title}||'%')

mapper.select:

       SELECT * FROM (
           SELECT ROWNUM rn, t.*
             FROM BOARD t


	AND rownum <= #{criteria.pageNum} * #{criteria.amount}
	ORDER BY BOARD_NO DESC
	)

       WHERE rn > (#{criteria.pageNum} - 1) * #{criteria.amount}
