mapper.update:
UPDATE MEMBER
PASSWORD = #{password}, MAIL = #{mail}, NAME = #{name}, RESUME = #{resume}, PROFILE_IMG = #{profileImg},
WHERE MEMBER_ID = #{memberId}

mapper.delete:
DELETE FROM MEMBER
WHERE  MEMBER_ID = #{memberId}
AND    PASSWORD = #{password}

mapper.update:
UPDATE MEMBER
SET EXP = EXP + #{exp}
WHERE MEMBER_ID = #{id}

UPDATE MEMBER
SET CASH_PT = CASH_PT + #{cashPt}
WHERE MEMBER_ID = #{id}

mapper.select:
select mail
from member
where member_id = #{id}
