mapper.update:
UPDATE PROJECT
PROJECT_NAME = #{projectName}, STATE = #{state}, PROCESS = #{process}, RECRUIT_EDT = #{recruitEdt}, SUBJECT = #{subject}, LANG = #{lang}, TOTAL_RCNT = #{totalRcnt}, PROJECT_SDT = #{projectSdt}, FRONT_RCNT = #{frontRcnt}, BACK_RCNT = #{backRcnt}, DESIGN_RCNT = #{designRcnt}, PROJECT_EDT = #{projectEdt}, FULL_RCNT = #{fullRcnt}, PLANNER_RCNT = #{plannerRcnt}, PROJECT_TERM = #{projectTerm}, APPLY_RCNT = #{applyRcnt}, LEADER_ID = #{leaderId},
WHERE PROJECT_NO = #{projectNo}

mapper.select:
SELECT *
  FROM PROJECT
 WHERE LEADER_ID = #{leaderId}
   AND STATE = #{state}
