mapper.update:
UPDATE PROJECT
PROJECT_NAME = #{projectName}, STATE = #{updateState}, PROCESS = #{process}, RECRUIT_EDT = #{recruitEdt}, SUBJECT = #{subject}, LANG = #{lang}, PROJECT_SDT = #{projectSdt}, PROJECT_EDT = #{projectEdt}, PROJECT_TERM = #{projectTerm},
WHERE PROJECT_NO = #{projectNo}

UPDATE PROJECT
CRNT_RCNT = CRNT_RCNT + 1, FRONT_RCNT = FRONT_RCNT - 1, BACK_RCNT = BACK_RCNT - 1, DESIGN_RCNT = DESIGN_RCNT - 1, FULL_RCNT = FULL_RCNT - 1, PLANNER_RCNT = PLANNER_RCNT - 1, STATE = #{updateState},
WHERE PROJECT_NO = #{projectNo}

sql.where:
STATE IN ('1') AND RECRUIT_EDT >= sysdate
AND STATE = '1' AND STATE = #{state}
AND PROJECT_NAME LIKE '%'||#{projectName}||'%' AND PROCESS = #{process}
AND LANG LIKE '%'||#{item}||'%'
AND PROJECT_TERM BETWEEN #{termStart} AND #{termEnd}
AND FRONT_RCNT > 0 AND BACK_RCNT > 0 AND DESIGN_RCNT > 0 AND FULL_RCNT > 0 AND PLANNER_RCNT > 0 AND PROJECT_TERM >= #{projectTerm}

mapper.select:

SELECT * FROM (
    SELECT ROWNUM rn, t.*
      FROM PROJECT t



    AND rownum <= #{criteria.pageNum} * #{criteria.amount}
)

WHERE rn > (#{criteria.pageNum} - 1) * #{criteria.amount}
