mapper.update:
UPDATE PROJECT_REQUEST
STATE = #{state}, SUBJECT = #{subject}, POSITION = #{position}, MEMBER_ID = #{memberId}, REQUEST_DT = #{requestDt},
WHERE PROJECT_NO = #{projectNo} AND MEMBER_ID = #{memberId}

mapper.delete:
DELETE FROM PROJECT_REQUEST
 WHERE PROJECT_NO = #{projectNo}
AND MEMBER_ID = #{memberId}

mapper.select:
SELECT *
  FROM PROJECT_REQUEST
 WHERE PROJECT_NO = #{projectNo}
   AND MEMBER_ID = #{memberId}
