mapper.insert:

	SELECT NVL(MAX(STUDY_NO),0)+1 AS "studyNo"
	FROM STUDY

INSERT INTO STUDY (STUDY_NO
				, LANG1
				, LOCATION
				, SUBJECT
				, UPLOAD_DATE
				, MAX_RCNT
				, DEFFICULTY
				, LEADER_ID
				, STUDY_SDT
				, STUDY_EDT
				, STATE
				, STUDY_NM
				, LANG2)

VALUES (		#{studyNo} 
				, #{lang1}
				, #{location}
				, #{subject}
				, SYSDATE 
				, #{maxRcnt}
				, #{defficulty}
				, #{leaderId}
				, #{studySdt}
				, #{studyEdt}
				, '0'
				, #{studyNm}
				, #{lang2})

mapper.update:
UPDATE STUDY
LANG1 = #{lang1}, LOCATION = #{location}, SUBJECT = #{subject}, MAX_RCNT = #{maxRcnt}, DEFFICULTY = #{defficulty}, LEADER_ID = #{leaderId}, STUDY_SDT = #{studySdt}, STUDY_EDT = #{studyEdt}, STUDY_NM = #{studyNm}, LANG2 = #{lang2},
WHERE STUDY_NO = #{studyNo}

mapper.insert:
INSERT INTO STUDY_INFO ( COLLEGE
						, CAREER
						, LICENSE
						, PRESENT
						, MEMBER_ID
						)

				VALUES (  #{college} 
						, #{career} 
						, #{license} 
						, #{present}
						, #{memberId}
 						)

mapper.update:
UPDATE STUDY_INFO
COLLEGE = #{college}, CAREER = #{career }, LICENSE = #{license}, PRESENT = #{present},
WHERE MEMBER_ID = #{memberId}

mapper.insert:
INSERT INTO STUDY_REQUEST (   STUDY_NO
							, MEMBER_ID
							, COLLEGE
							, CAREER
							, LICENSE
							, PRESENT
							, PROFILE_IMG
						   )

				   VALUES (  #{studyNo}
				   		   , #{memberId}
				   		   , #{college}
				   		   , #{career}
				   		   , #{license}
				   		   , #{present}
				   		   , #{profileImg}
				   		   )

INSERT INTO STUDY_REQUEST (   STUDY_NO
							, STATE
							, MEMBER_ID
							, ISLEADER
						   )

				   VALUES (  #{studyNo}
				   		   , 1
				   		   , #{memberId}
				   		   , 1
				   		   )

mapper.update:
UPDATE STUDY_REQUEST
SET STATE = 1
WHERE MEMBER_ID = #{memberId}
AND
STUDY_NO = #{studyNo}

UPDATE STUDY_REQUEST
SET STATE = 3
WHERE MEMBER_ID = #{memberId}
AND
STUDY_NO = #{studyNo}

mapper.delete:
DELETE
FROM STUDY_REQUEST
WHERE MEMBER_ID = #{memberId}
AND
STUDY_NO = #{studyNo}

mapper.select:

        SELECT * FROM (
            SELECT ROWNUM rn, t.*
              FROM STUDY t




	            AND rownum <= #{criteria.pageNum} * #{criteria.amount}

        )

        WHERE rn > (#{criteria.pageNum} - 1) * #{criteria.amount}
